or.sqlCheck:
select count(name) from
simpledomain
where name='database_version';

changeSet.sql:
update prescription pres set clinic = currentclinic from (select ep.clinic as currentclinic, pre.id as pid from episode ep, prescription pre where ep.patient = pre.patient and pre.date BETWEEN ep.startDate and ep.stopDate group by pid, currentclinic order by currentclinic, pid) as foo where pres.id = foo.pid;
update prescription pres set clinic = currentclinic from (select ep.clinic as currentclinic, pre.id as pid from episode ep, prescription pre where ep.patient = pre.patient and pre.date >= ep.startDate and pre.clinic is null group by pid, currentclinic order by currentclinic, pid) as foo where pres.id = foo.pid;
update prescription pres set clinic = currentclinic from (select ep.clinic as currentclinic, pre.id as pid from episode ep, prescription pre where ep.patient = pre.patient and pre.clinic is null group by pid, currentclinic order by currentclinic, pid) as foo where pres.id = foo.pid;

INSERT INTO simpledomain(id, description, name, value) select
nextval('hibernate_sequence'),'', 'activation_reason', 
'Restart at Down Referral Clinic';

